mapper.select:
select count(*)
from BOARD
where WORK_SPACE_ID = #{workSpaceId}
  and CATEGORY_ID = #{categoryId}

select count(*)
from CHECKED_BOARD
where BOARD_ID = #{boardId}
  and MEMBER_ID = #{memberId}

select count(*)
from BOARD
where ID = #{boardId}
  and WORK_SPACE_ID = #{workSpaceId}

select count(*)
from COMMENTS
where ID = #{commentsId}
  and MEMBER_ID = #{memberId}

select count(*)
from BOARD
where ID = #{boardId}
  and MEMBER_ID = #{memberId}

mapper.update:
update BOARD set


        , TITLE = #{title}


        , CONTENT = #{content}


        , PLAN_DATE = #{planDate}


where ID = #{boardId}
