mapper.select:
select count(*)
from TEAM_MEMBER
where WORK_SPACE_ID = #{workSpaceId}

mapper.update:
update WORK_SPACE
set INVITATION_CODE = #{invitationCode}
where ID = #{id}
  and MEMBER_ID = #{memberId}

update WORK_SPACE
set NAME       = #{name},
    HEAD_COUNT = #{headCount}
where ID = #{id}
  and MEMBER_ID = #{memberId}

mapper.delete:
delete
from WORK_SPACE
where ID = #{workSpaceId}
  and MEMBER_ID = #{memberId}

delete
from TEAM_MEMBER
where WORK_SPACE_ID = #{workSpaceId}
  and MEMBER_ID = #{memberId}
